import{_ as e,c as a,o as l,a2 as r}from"./chunks/framework.CJqd0DOR.js";const i="/three-tile-doc/assets/image-20240708192901949.B4HVb3DZ.png",s="/three-tile-doc/assets/image-20240708195023555.C1_nKJQb.png",h="/three-tile-doc/assets/image-20240708194820946.Cd416TRP.png",n="/three-tile-doc/assets/image-20240708193035753.BFqdiR-Z.png",o="/three-tile-doc/assets/image-1.BD3WAt85.png",g="/three-tile-doc/assets/image-2.CZhGOdOl.png",d="/three-tile-doc/assets/image-3.ufB7Z9Fh.png",m="/three-tile-doc/assets/image-5.D-mWT2Gp.png",c="/three-tile-doc/assets/image-6.mGWBBz9c.png",p="/three-tile-doc/assets/image-20240708212408563.zoEy-tZW.png",_="/three-tile-doc/assets/image-8.QPwszKk6.png",x="/three-tile-doc/assets/image-9.96z75i55.png",b="/three-tile-doc/assets/image-20240708161740228.q_bUKFR_.png",y="/three-tile-doc/assets/image-20240708161859490.AuPZMi1l.png",u="/three-tile-doc/assets/image-20240708162035091.x5YqOj_Q.png",f="/three-tile-doc/assets/image-20240708162005274.P_H0dgE6.png",j="/three-tile-doc/assets/image-20240708162232109.iTTW514r.png",z="/three-tile-doc/assets/image-20240708162315798.BOPH8h_Y.png",k="/three-tile-doc/assets/image-20240708162456709.DGQJUzJV.png",q="/three-tile-doc/assets/image-20240708162928975.DhzT1RNi.png",P="/three-tile-doc/assets/image-20240708163206668.z39lAzc8.png",w="/three-tile-doc/assets/image-20240708163314611.sDAKjh0c.png",D="/three-tile-doc/assets/image-20240708215113203.BrbcduEk.png",T="/three-tile-doc/assets/image-20240708174444216.C7zXeOfg.png",B="/three-tile-doc/assets/image-20240708174650304.BCV6lRjd.png",C="/three-tile-doc/assets/image-20240708174850505.CPtaTKVo.png",O="/three-tile-doc/assets/image-20240708174735229.D6zGzSZX.png",N=JSON.parse('{"title":"three-tile 介绍","description":"","frontmatter":{},"headers":[],"relativePath":"1.introduce/01.whatIs/index.md","filePath":"1.introduce/01.whatIs/index.md","lastUpdated":1761368239000}'),S={name:"1.introduce/01.whatIs/index.md"};function V(A,t,G,R,Z,v){return l(),a("div",null,t[0]||(t[0]=[r('<h1 id="three-tile-介绍" tabindex="-1">three-tile 介绍 <a class="header-anchor" href="#three-tile-介绍" aria-label="Permalink to &quot;three-tile 介绍&quot;">​</a></h1><p>three-tile 是一个开源的轻量级前端三维瓦片框架，基于 threejs 使用 typescript 开发，提供一个三维地形模型，能轻松给你的应用增加三维瓦片地图。经过数十版迭代，目前框架功能、效果能够满足生产环境使用，已在多个项目中得到了验证和应用。</p><table tabindex="0"><thead><tr><th><img src="'+i+'" alt="" loading="lazy"></th><th><img src="'+s+'" alt="" loading="lazy"></th><th><img src="'+h+'" alt="" loading="lazy"></th><th><img src="'+n+'" alt="" loading="lazy"></th></tr></thead><tbody><tr><td></td><td></td><td></td><td></td></tr></tbody></table><ul><li><p>框架源码：<a href="https://github.com/sxguojf/three-tile" target="_blank" rel="noreferrer">https://github.com/sxguojf/three-tile</a></p></li><li><p>Vue 演示：<a href="https://sxguojf.github.io/mydemo/three-tile-vue/index.html" target="_blank" rel="noreferrer">https://sxguojf.github.io/mydemo/three-tile-vue/index.html</a></p></li><li><p>问题讨论：<a href="https://github.com/sxguojf/three-tile/issues" target="_blank" rel="noreferrer">https://github.com/sxguojf/three-tile/issues</a></p></li></ul><div class="warning custom-block"><p class="custom-block-title">免责声明：</p><p>本框架不含任何地图数据，示例和演示程序中使用的地图均为直接挂接第三方数据，并不代表本开发框架立场。开发者请遵循相关法律规定使用合法地图。</p></div><h2 id="_1-three-tile-是什么" tabindex="-1">1. three-tile 是什么？ <a class="header-anchor" href="#_1-three-tile-是什么" aria-label="Permalink to &quot;1. three-tile 是什么？&quot;">​</a></h2><ul><li>three-tile 是基于 threejs 开发的一套前端三维瓦片地图框架。</li><li>three-tile 是基于 cesium、mapbox 等的二次封装吗？No，它是基于 threejs 自主实现的。</li><li>three-tile 更像是游戏开发中的 LOD 地形，但它可以使用地图服务商提供的地形和影像瓦片数据渲染地图。</li><li>three-tile 是 3D WebGis？No，它的 GIS 功能较弱，以数据可视化为主，无地理信息数据分析功能。</li><li>three-tile 的核心实际与地图无关，它就是一个 LOD 模型，地图只是其典型应用之一。</li></ul><h2 id="_2-three-tile-的特点" tabindex="-1">2. three-tile 的特点 <a class="header-anchor" href="#_2-three-tile-的特点" aria-label="Permalink to &quot;2. three-tile 的特点&quot;">​</a></h2><p>相比市面上常见三维地图，three-tile 有如下特点：</p><ul><li>轻量级：地图以一个三维模型方式提供，使用它不会对已有程序架构产生任何影响。</li><li>依赖少：整个框架仅有 threejs（&gt;=R171）一个依赖。</li><li>效果好：虽然很小巧，但地图显示效果不输 cesium 等重量级框架。</li><li>速度快：对资源占用做极致优化，核显也能轻松跑到 60FPS。</li><li>使用简单：熟悉 threejs 基本上没有学习成本。</li><li>扩展性强：插件架构，数据格式、模型、纹理、材质、渲染过程均能根据自己需要扩展和替换。</li></ul><h2 id="_3-应用场景" tabindex="-1">3. 应用场景 <a class="header-anchor" href="#_3-应用场景" aria-label="Permalink to &quot;3. 应用场景&quot;">​</a></h2><h3 id="_3-1-给现有应用增加地形" tabindex="-1">3.1 给现有应用增加地形 <a class="header-anchor" href="#_3-1-给现有应用增加地形" aria-label="Permalink to &quot;3.1 给现有应用增加地形&quot;">​</a></h3><p>在 threejs 示例中，大部分简单应用使用一个平面作为地面，如果能把平面换为真实地形，立马增色不少：</p><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+o+'" alt="alt text" loading="lazy"></th><th style="text-align:center;"><img src="'+g+'" alt="alt text" loading="lazy"></th><th style="text-align:center;"><img src="'+d+'" alt="" loading="lazy"></th><th style="text-align:center;"><img src="'+m+'" alt="alt text" loading="lazy"></th></tr></thead><tbody><tr><td style="text-align:center;">threejs 中的傻鸟</td><td style="text-align:center;">加上地形，让它翱翔在青藏高原</td><td style="text-align:center;">threejs 中可爱的小房子</td><td style="text-align:center;">西安南二环找块工地放上去</td></tr><tr><td style="text-align:center;"><a href="https://threejs.org/examples/#webgl_lights_hemisphere" target="_blank" rel="noreferrer">three.js examples (threejs.org)</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step3.2/index.html" target="_blank" rel="noreferrer">three-tile demo (sxguojf.github.io)</a></td><td style="text-align:center;"><a href="https://threejs.org/examples/#webgl_animation_keyframes" target="_blank" rel="noreferrer">three.js examples (threejs.org)</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step2.10/index.html" target="_blank" rel="noreferrer">three-tile demo (sxguojf.github.io)</a></td></tr><tr><td style="text-align:center;"><img src="'+c+'" alt="alt text" loading="lazy"></td><td style="text-align:center;"><img src="'+p+'" alt="image-20240708212408563" loading="lazy"></td><td style="text-align:center;"><img src="'+_+'" alt="alt text" loading="lazy"></td><td style="text-align:center;"><img src="'+x+'" alt="alt text" loading="lazy"></td></tr><tr><td style="text-align:center;">threejs 中孤独的小兵兵</td><td style="text-align:center;">放到地图上执行任务</td><td style="text-align:center;">threejs 中平淡无奇的夕阳</td><td style="text-align:center;">加上地形才是它真正的效果</td></tr><tr><td style="text-align:center;"><a href="https://threejs.org/examples/#webgl_animation_skinning_blending" target="_blank" rel="noreferrer">three.js examples (threejs.org)</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step3.4/index.html" target="_blank" rel="noreferrer">three-tile demo (sxguojf.github.io)</a></td><td style="text-align:center;"><a href="https://threejs.org/examples/#webgl_shaders_sky" target="_blank" rel="noreferrer">three.js examples (threejs.org)</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step2.11/index.html" target="_blank" rel="noreferrer">three-tile demo (sxguojf.github.io)</a></td></tr></tbody></table><p>而这一切使用 three-tile 仅需三步：</p><ol><li>定义地图数据源</li><li>创建地图模型</li><li>用地图替换原来的平面</li></ol><h3 id="_3-2-简单的-webgis" tabindex="-1">3.2 简单的 webgis <a class="header-anchor" href="#_3-2-简单的-webgis" aria-label="Permalink to &quot;3.2 简单的 webgis&quot;">​</a></h3><p>当然，你如果要把 three-tile 当做一个简单三维 gis 也未尝不可，three-tile 地图模型可直接使用主流瓦片数据源，渲染出逼真的地形；提供地理坐标（经纬度海拔高度）到三维场景坐标的转换，能将地图元素（模型、标签）叠加在指定位置；通过鼠标键盘控制摄像机，实现地图缩放、平移、旋转和漫游；内置 mapbox、bing、goole、arcgis、天地图、高德、腾讯等瓦片地图支持，也可以自行扩展支持其它瓦片地图服务。</p><table tabindex="0"><thead><tr><th><img src="'+b+'" alt="image-20240708161740228" loading="lazy"></th><th><img src="'+y+'" alt="image-20240708161859490" loading="lazy"></th><th><img src="'+u+'" alt="image-20240708162035091" loading="lazy"></th><th><img src="'+f+'" alt="image-20240708162005274" loading="lazy"></th></tr></thead><tbody><tr><td><img src="'+j+'" alt="image-20240708162232109" loading="lazy"></td><td><img src="'+z+'" alt="image-20240708162315798" loading="lazy"></td><td><img src="'+k+'" alt="image-20240708162456709" loading="lazy"></td><td><img src="'+q+'" alt="image-20240708162928975" loading="lazy"></td></tr></tbody></table><h3 id="_3-3-游戏开发" tabindex="-1">3.3 游戏开发 <a class="header-anchor" href="#_3-3-游戏开发" aria-label="Permalink to &quot;3.3 游戏开发&quot;">​</a></h3><p>如果你想将 three-tile 用于游戏开发，也可以试试，它完美支持 threejs 内置的各种控制器，仅更换控制器，即可实现第一人称、飞行等游戏功能，在真实地图上开战效果应该不错。</p><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+P+'" alt="image-20240708163206668" loading="lazy">第一人称射击</th><th style="text-align:center;"><img src="'+w+'" alt="image-20240708163314611" loading="lazy">即时战略</th><th style="text-align:center;"><img src="'+D+'" alt="image-20240708215113203" loading="lazy">模拟飞行</th></tr></thead><tbody><tr><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step3.3/index.html" target="_blank" rel="noreferrer">three-tile demo</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step3.4/index.html" target="_blank" rel="noreferrer">three-tile demo</a></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step3.2/index.html" target="_blank" rel="noreferrer">three-tile demo</a></td></tr></tbody></table><h3 id="_3-4-数据可视化" tabindex="-1">3.4 数据可视化 <a class="header-anchor" href="#_3-4-数据可视化" aria-label="Permalink to &quot;3.4 数据可视化&quot;">​</a></h3><p>目前，three-tile 在生产环境的应用主要是数据可视化：</p><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+T+'" alt="image-20240708174444216" loading="lazy">三维卫星云图</th><th style="text-align:center;"><img src="'+B+'" alt="image-20240708174650304" loading="lazy">风场动画</th><th style="text-align:center;"><img src="'+C+'" alt="" loading="lazy">500hpa 高度场</th><th style="text-align:center;"><img src="'+O+'" alt="" loading="lazy">pm2.5 体渲染</th></tr></thead><tbody><tr><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step5.1/index.html" target="_blank" rel="noreferrer">three-tile demo</a></td><td style="text-align:center;"></td><td style="text-align:center;"><a href="https://sxguojf.github.io/three-tile-example/step5.4/index.html" target="_blank" rel="noreferrer">three-tile demo</a></td><td style="text-align:center;"></td></tr></tbody></table>',25)]))}const W=e(S,[["render",V]]);export{N as __pageData,W as default};
