import{_ as d,c as e,o as r,a2 as a}from"./chunks/framework.BQo5SrHh.js";const u=JSON.parse('{"title":"3. TileMap 属性","description":"","frontmatter":{},"headers":[],"relativePath":"2.base/03.mapProps/index.md","filePath":"2.base/03.mapProps/index.md","lastUpdated":1761902788000}'),o={name:"2.base/03.mapProps/index.md"};function n(i,t,l,p,s,m){return r(),e("div",null,t[0]||(t[0]=[a('<h1 id="_3-tilemap-属性" tabindex="-1">3. TileMap 属性 <a class="header-anchor" href="#_3-tilemap-属性" aria-label="Permalink to &quot;3. TileMap 属性&quot;">​</a></h1><p>TileMap 继承于 threejs 的 Object3D ，除了 Object3D 的属性外，它还有如下属性：</p><table tabindex="0"><thead><tr><th>名称</th><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>name</td><td>string</td><td>取得或设置模型名称，默认为&#39;map&#39;</td></tr><tr><td>imgSource</td><td>ISource | ISource[]</td><td>取得或设置影像数据，必填参数源</td></tr><tr><td>demSource</td><td>ISource | undefined</td><td>取得或设置地形数据，默认为 undefined 源</td></tr><tr><td>LODThreshold</td><td>number</td><td>设置瓦片 LOD 阈值系数。TileMap 本质上是一个动态 LOD 模型，它根据瓦片离摄像机的距离对模型进行细化或合并，该值越大瓦片细化越快，越小瓦片合并越快，默认为 1</td></tr><tr><td>rootTile</td><td>Tile</td><td>取得根瓦片</td></tr><tr><td>loader</td><td>ITileLoader</td><td>取得或设置地图瓦片加载器，默认为内置瓦片加载器</td></tr><tr><td>minLevel</td><td>number</td><td>取得或设置地图最小缩放级别，小于这个级别瓦片树不再更新，默认为 2</td></tr><tr><td>lon0</td><td>ProjectCenterLongitude</td><td>取得或设置中央子午线经度，中央子午线决定了地图的投影中心经度，可设置为-90，0，90，默认为 0</td></tr><tr><td>bounds</td><td>[number, number, number, number]</td><td>取得地图显示范围，默认为[-180, -90, 180, 90]，即世界范围</td></tr><tr><td>projection</td><td>IProjection</td><td>取得地图投影对象，默认为 EPSG:3857 投影</td></tr><tr><td>autoUpdate</td><td>boolean</td><td>取得或设置瓦片是否在每帧渲染时自动更新，默认为 true</td></tr><tr><td>downloading</td><td>number</td><td>取得当前正在下载的瓦片数量</td></tr></tbody></table>',3)]))}const _=d(o,[["render",n]]);export{u as __pageData,_ as default};
